export * from "../proto.js";
import { int32, Error, Stmt, StmtResult, Batch, BatchResult, DescribeResult } from "../proto.js";
export type PipelineRequestBody = {
    "baton": string | null;
    "requests": Array<StreamRequest>;
};
export type PipelineResponseBody = {
    "baton": string | null;
    "base_url": string | null;
    "results": Array<StreamResult>;
};
export type StreamResult = StreamResultOk | StreamResultError;
export type StreamResultOk = {
    "type": "ok";
    "response": StreamResponse;
};
export type StreamResultError = {
    "type": "error";
    "error": Error;
};
export type StreamRequest = CloseStreamReq | ExecuteStreamReq | BatchStreamReq | SequenceStreamReq | DescribeStreamReq | StoreSqlStreamReq | CloseSqlStreamReq;
export type StreamResponse = CloseStreamResp | ExecuteStreamResp | BatchStreamResp | SequenceStreamResp | DescribeStreamResp | StoreSqlStreamResp | CloseSqlStreamResp;
export type CloseStreamReq = {
    "type": "close";
};
export type CloseStreamResp = {
    "type": "close";
};
export type ExecuteStreamReq = {
    "type": "execute";
    "stmt": Stmt;
};
export type ExecuteStreamResp = {
    "type": "execute";
    "result": StmtResult;
};
export type BatchStreamReq = {
    "type": "batch";
    "batch": Batch;
};
export type BatchStreamResp = {
    "type": "batch";
    "result": BatchResult;
};
export type SequenceStreamReq = {
    "type": "sequence";
    "sql"?: string | null;
    "sql_id"?: int32 | null;
};
export type SequenceStreamResp = {
    "type": "sequence";
};
export type DescribeStreamReq = {
    "type": "describe";
    "sql"?: string | null;
    "sql_id"?: int32 | null;
};
export type DescribeStreamResp = {
    "type": "describe";
    "result": DescribeResult;
};
export type StoreSqlStreamReq = {
    "type": "store_sql";
    "sql_id": int32;
    "sql": string;
};
export type StoreSqlStreamResp = {
    "type": "store_sql";
};
export type CloseSqlStreamReq = {
    "type": "close_sql";
    "sql_id": int32;
};
export type CloseSqlStreamResp = {
    "type": "close_sql";
};
