export type int32 = number;
export type Error = {
    "message": string;
    "code"?: string | null;
};
export type Stmt = {
    "sql"?: string | undefined;
    "sql_id"?: int32 | undefined;
    "args"?: Array<Value>;
    "named_args"?: Array<NamedArg>;
    "want_rows": boolean;
};
export type NamedArg = {
    "name": string;
    "value": Value;
};
export type StmtResult = {
    "cols": Array<Col>;
    "rows": Array<Array<Value>>;
    "affected_row_count": number;
    "last_insert_rowid"?: string | null;
};
export type Col = {
    "name": string | null;
    "decltype"?: string | null;
};
export type Batch = {
    "steps": Array<BatchStep>;
};
export type BatchStep = {
    "condition"?: BatchCond | null;
    "stmt": Stmt;
};
export type BatchResult = {
    "step_results": Array<StmtResult | null>;
    "step_errors": Array<Error | null>;
};
export type BatchCond = {
    "type": "ok";
    "step": int32;
} | {
    "type": "error";
    "step": int32;
} | {
    "type": "not";
    "cond": BatchCond;
} | {
    "type": "and";
    "conds": Array<BatchCond>;
} | {
    "type": "or";
    "conds": Array<BatchCond>;
};
export type DescribeResult = {
    "params": Array<DescribeParam>;
    "cols": Array<DescribeCol>;
    "is_explain": boolean;
    "is_readonly": boolean;
};
export type DescribeParam = {
    "name": string | null;
};
export type DescribeCol = {
    "name": string;
    "decltype": string | null;
};
export type Value = {
    "type": "null";
} | {
    "type": "integer";
    "value": string;
} | {
    "type": "float";
    "value": number;
} | {
    "type": "text";
    "value": string;
} | {
    "type": "blob";
    "base64": string;
};
